import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { uiActions } from "./store/ui-slice";
import Notification from "./components/UI/Notifiation";
function App() {
  const dispatch = useDispatch();

  const showCart = useSelector((state) => state.ui.cartIsvisible);
  const cart = useSelector((state) => state.cart);

  const notification = useSelector((state) => state.ui.notification);

  useEffect(() => {
    const sendCart = async () => {
      dispatch(
        uiActions.showNotificaion({
          status: "pending",
          title: "Sending...",
          message: "Sending cart",
        })
      );
      const response = await fetch(
        "https://react-food-order-app-4a405-default-rtdb.asia-southeast1.firebasedatabase.app/reduxCart.json",
        { method: "PUT", body: JSON.stringify(cart) }
      );
      if (!response.ok) throw new Error("Sending cart datat failed!");
      const data = await response.json();
    };
    sendCart().catch((error) => {
      dispatch(
        uiActions.showNotificaion({
          status: "error",
          title: "Error!",
          message: "Sending cart data failed!",
        })
      );
    });
  }, [cart, dispatch]);

  return (
    <>
      {notification && (
        <Notification
          title={notification.title}
          message={notification.message}
        />
      )}
      <Layout>
        {showCart && <Cart />}
        <Products />
      </Layout>
    </>
  );
}

export default App;
