import { createBrowserRouter } from "react-router-dom";
import {
  EditEventPage,
  ErrorPage,
  EventDetailPage,
  EventsPage,
  HomePage,
  NewEventPage,
  NewsletterPage,
} from "../pages";
import { EventLayout, RootLayout } from "../layouts";
import { eventLoader } from "../pages/EventsPage";
import { eventItemDelete, eventItemLoader } from "../pages/EventDetailPage";
import { manipulateEventAction } from "../components/EventForm";
import { newsletterAction } from "../pages/NewsletterPage";

export const router = createBrowserRouter([
  {
    path: "/",
    element: <RootLayout />,
    errorElement: <ErrorPage />,
    children: [
      { index: true, element: <HomePage /> },
      {
        path: "events",
        element: <EventLayout />,
        children: [
          {
            index: true,
            element: <EventsPage />,
            loader: eventLoader,
          },
          {
            path: "new",
            element: <NewEventPage />,
            action: manipulateEventAction,
          },
          {
            path: ":eventID",
            id: "event-detail",
            loader: eventItemLoader,
            children: [
              {
                index: true,
                element: <EventDetailPage />,
                action: eventItemDelete,
              },
              {
                path: "edit",
                element: <EditEventPage />,
                action: manipulateEventAction,
              },
            ],
          },
        ],
      },
      {
        path: "newsletter",
        element: <NewsletterPage />,
        action: newsletterAction,
      },
    ],
  },
]);
