import { redirect } from "react-router-dom";

export const getToken = () => {
  const token = localStorage.getItem("token");
  return token;
};

export const logoutAction = () => {
  localStorage.clear();
  return redirect("/");
};

// auth to compnents

export const tokenLoader = () => {
  return getToken();
};
