import { useEffect, useState } from "react";
import useInput from "../hooks/use-input";

const SimpleInput = (props) => {
  const {
    value: nameValue,
    hasError: nameError,
    valueChangeHandler: nameChangeHandler,
    inputBlurHandler: nameBlurHandler,
  } = useInput((value) => value.trim() !== "");

  // name
  const [name, setName] = useState("");
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);

  // email
  const [email, setEmail] = useState("");
  const [enteredEmailTouched, setEnteredEmailTouched] = useState(false);

  const emailRegx =
    /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/;

  const enteredEmailIsValid = emailRegx.test(email.trim());
  const emailIsInvalid = !enteredEmailIsValid && enteredEmailTouched;

  let formIsValid = false;

  if (enteredNameIsValid && enteredEmailIsValid) {
    formIsValid = true;
  }

  const inputHandler = (event) => {
    event.target.id === "name" && setName(event.target.value);
    event.target.id === "email" && setEmail(event.target.value);
  };

  const inputBlurHandler = (event) => {
    event.target.id === "name" && setEnteredNameTouched(true);
    event.target.id === "email" && setEnteredEmailTouched(true);
  };

  const onSubmitHandler = (event) => {
    event.preventDefault();
    setEnteredNameTouched(true);
    if (!enteredNameIsValid || !enteredEmailIsValid) {
      return;
    }

    console.log(name);
    console.log(email);

    setName("");
    setEnteredNameTouched(false);

    setEmail("");
    setEnteredEmailTouched(false);

    // nameInputRef.current.value = "";     /* Manipulates the DOM directly */
  };

  return (
    <form onSubmit={onSubmitHandler}>
      <div className={nameIsInvalid ? "form-control invalid" : "form-control"}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={inputHandler}
          onBlur={inputBlurHandler}
          value={name}
        />
        {nameIsInvalid && <p className="error-text">Name mustn't be empty</p>}
      </div>
      <div className={emailIsInvalid ? "form-control invalid" : "form-control"}>
        <label htmlFor="name">Your Email</label>
        <input
          type="email"
          id="email"
          onChange={inputHandler}
          onBlur={inputBlurHandler}
          value={email}
        />
        {emailIsInvalid && <p className="error-text">Invalid email</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
