import { useState } from "react";

const SimpleInput = (props) => {
  const [name, setName] = useState("");
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);

  const enteredNameIsValid = name.trim() !== "";
  const nameIsInvalid = !enteredNameIsValid && enteredNameTouched;

  const inputHandler = (event) => {
    setName(event.target.value);
  };

  const inputBlurHandler = (event) => {
    setEnteredNameTouched(true);
  };

  const onSubmitHandler = (event) => {
    event.preventDefault();
    setEnteredNameTouched(true);
    if (!enteredNameIsValid) {
      return;
    }
    console.log(name);
    setName("");
    // nameInputRef.current.value = "";     /* Manipulates the DOM directly */
  };

  const validateStyle = nameIsInvalid ? "form-control invalid" : "form-control";

  return (
    <form onSubmit={onSubmitHandler}>
      <div className={validateStyle}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={inputHandler}
          onBlur={inputBlurHandler}
          value={name}
        />
        {nameIsInvalid && <p className="error-text">Name mustn't be empty</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
