import { useRef, useState } from "react";

const SimpleInput = (props) => {
  const nameInputRef = useRef();

  const [name, setName] = useState("");
  const [nameInputIsValid, setNameInputIsValid] = useState(false);
  const [nameInputTouched, setNameInputTouched] = useState(false);

  const inputHandler = (event) => {
    setName(event.target.value);
  };

  const onSubmitHandler = (event) => {
    event.preventDefault();
    setNameInputTouched(true);
    if (name.trim() === "") {
      setNameInputIsValid(false);
      return;
    }
    setNameInputIsValid(true);
    console.log(name);
    console.log(nameInputRef.current.value);
    setName("");
    // nameInputRef.current.value = "";     /* Manipulates the DOM directly */
  };

  const nameIsInvalid = !nameInputIsValid && nameInputTouched;
  const validateStyle = nameIsInvalid ? "form-control invalid" : "form-control";

  return (
    <form onSubmit={onSubmitHandler}>
      <div className={validateStyle}>
        <label htmlFor="name">Your Name</label>
        <input
          ref={nameInputRef}
          type="text"
          id="name"
          onChange={inputHandler}
          onBlur={inputBlurHandler}
          value={name}
        />
        {nameIsInvalid && <p className="error-text">Name mustn't be empty</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
